/*
https://leetcode.com/problems/sum-of-even-numbers-after-queries/
*/

/*

A []int, queries []queries
val = queries[i][0], index = queries[i][1],
add val to A[index]

1 <= A.length <= 10000
-10000 <= A[i] <= 10000
1 <= queries.length <= 10000
-10000 <= queries[i][0] <= 10000
0 <= queries[i][1] < A.length
*/

func sumEvenAfterQueries(A []int, queries [][]int) []int {
	val := 0
	index := 0
	ALen := len(A)
	ans := make([]int, ALen, ALen)

	/*
		sum을 한번만 계산하고, 새로운 수정이 있으면 업데이트
		중첩 반복문은 시간을 더 잡아 먹는다
	*/
	for idx, query := range queries {
		var sum int
		val = query[0]
		index = query[1]

		A[index] += val
		// 합계는 O(n) process인데, 이걸 매번하고 있으므로 O(mn) 시간이 소요된다
		for _, valueOfA := range A {
			mod := math.Mod(float64(valueOfA), float64(2))
			if mod == 0 {
				sum += valueOfA
			}
		}

		if !math.IsNaN(float64(sum)) {
			ans[idx] = sum
		}
		// fmt.Println("sum: ", sum)
	}

	return ans
}

func sumEvenAfterQueriesImproved(A []int, queries [][]int) []int {
	val := 0
	index := 0
	ALen := len(A)
	ans := make([]int, ALen, ALen)
	sum := 0

	for idx, query := range queries {
		val = query[0]
		index = query[1]

		original := A[index]
		calculated := A[index] + val
		A[index] = calculated
		if idx == 0 {
			A[index] = calculated
			for _, valueOfA := range A {
				mod := math.Mod(float64(valueOfA), float64(2))
				if mod == 0 {
					sum += valueOfA
				}
			}
			ans[idx] = sum
		} else {
			if math.Mod(float64(calculated), float64(2)) == 0 {
				// 원본 값이 짝수였으면 원본 값을 제거하고 계산된 값을 다시 넣는다
				if math.Mod(float64(original), float64(2)) == 0 {
					// 더해진 것을 빼는 것도, 뺀 것을 다시 더하는 것도, 되돌리는 것은 '-' 통해 이뤄진다
					sum -= original
				}
				sum += calculated
				ans[idx] = sum
			} else {
				// original이 홀수였으면 원래 계산을 안했다
				if math.Mod(float64(original), float64(2)) == 0 {
					// 더해진 것을 빼는 것도, 뺀 것을 다시 더하는 것도, 되돌리는 것은 '-' 통해 이뤄진다
					sum -= original
				}
				ans[idx] = sum
			}
		}
	}

	return ans
}

